import matplotlib.pyplot as plt
from shapely.wkt import loads


def display_polygons(polygons_wkt):
    """
    Displays an array of Shapely polygons using Matplotlib.
    
    Args:
        polygons_wkt (list): List of WKT representations of the polygons.
    """
    fig, ax = plt.subplots()
    
    for polygon_wkt in polygons_wkt:
        polygon = loads(polygon_wkt)
        x, y = polygon.exterior.xy
        ax.plot(x, y, linewidth=2)
        ax.fill(x, y, alpha=0.2)
    
    ax.set_aspect('equal', adjustable='box')
    #plt.show()


